targets.expr:
group by (cluster_name, source_namespace, source_pod, source_container, source_controller, destination_ip, destination_port) (
  label_replace(
    label_replace(
      label_replace(
        label_replace(
          label_replace(
            label_replace(
            container_net_tcp_successful_connects_total{
                cluster_name=~"$cluster", container_id=~"/k8s/${source_namespace}/.*",
            },
            "destination_ip", "$1", "actual_destination", "([^:]+):.*"
            ),
            "destination_port", "$1", "actual_destination", "[^:]+:([0-9]+)"
          ),
          "source_container", "$4", "container_id", "/([^/]+)/([^/]+)/([^/]+)/([^/]+)"
        ),
        "source_pod", "$3", "container_id", "/([^/]+)/([^/]+)/([^/]+)/([^/]+)"
      ),
      "source_namespace", "$2", "container_id", "/([^/]+)/([^/]+)/([^/]+)/([^/]+)"
    ),
    "source_controller", "$1", "source_pod", "(.+?)(-[^-]+)?$"
  )
)
* on (destination_ip, cluster_name) group_left(fqdn) (
  group by (cluster_name, destination_ip, fqdn) (
    label_replace(
        topk(1, ip_to_fqdn{cluster_name=~"$cluster"}) by (ip),
        "destination_ip", "$1", "ip", "(.*)"
    )
  )
)

