WITH (
  metric = max by (source, actual_destination, status) (
    label_replace(
      sum by (container_id, actual_destination) (
        rate(container_http_requests_total{cluster_name=~"$cluster", container_id=~"\\/k8s.*", status!~"2.."}[1m])
      ) / on (container_id, actual_destination)
      sum by (container_id, actual_destination) (
        rate(container_http_requests_total{container_id=~"\\/k8s.*"}[1m])
      ),
      "source", "$1", "container_id", "\\/k.+?\\/.*\\/(.+?)\\/.*"
    )
    or label_replace(
      sum by (container_id, actual_destination) (
        rate(container_http_requests_total{cluster_name=~"$cluster", container_id=~"\\/system.*", status!~"2.."}[1m])
      ) / on (container_id, actual_destination)
      sum by (container_id, actual_destination) (
        rate(container_http_requests_total{container_id=~"\\/system.*"}[1m])
      ),
      "source", "$1", "container_id", "\\/system.slice\\/(.+?)$"
    )
  ),
  edges_query = group by (source, actual_destination, target_namespace, target_pod, target_service) (
    label_replace(
      label_copy(
        coroot:http_requests{
          target_namespace=~"$namespace",
          target_service=~"$service"
        },
        "source_pod", "source"
      ),
      "actual_destination", "$1", "actual_destination", "(.+?)"
    )
  )
)

max by (container_id) (
  (
    metric * on(source, actual_destination)
    group_left(target_namespace, target_pod, target_service)
    edges_query
  )
  * on(target_pod) group_left(container_id) (
    group by (target_pod, container_id) (
      label_replace(
        container_restarts_total{cluster_name=~"$cluster", container_id=~"\\/k.*"},
        "target_pod", "$1", "container_id", "\\/k.+?\\/.*\\/(.+?)\\/.*"
      )
      or on (target_pod, container_id) label_replace(
        container_restarts_total{cluster_name=~"$cluster", container_id=~"\\/system.*"},
        "target_pod", "$1", "container_id", "\\/sys.+?\\/(.+?)$"
      )
    )
  )
)

WITH (
  metric = max by (source, actual_destination) (
    label_replace(
      max_over_time(histogram_quantile(0.95, sum by (le, container_id,actual_destination)(
    rate( container_http_requests_duration_seconds_total_bucket{cluster_name=~"$cluster"}[$__interval]))
    )[$__range]),
      "source", "$1", "container_id", "\\/k.+?\\/.*\\/(.+?)\\/.*"
    ) or label_replace(
     max_over_time(histogram_quantile(0.95, sum by (le, container_id,actual_destination)(
    rate( container_http_requests_duration_seconds_total_bucket{cluster_name=~"$cluster"}[$__interval]))
    )[$__range]),
      "source", "$1", "container_id", "\\/system.slice\\/(.+?)$"
    )
  ), 
  edges_query =  group by(source,actual_destination,target_namespace,target_pod,target_service)(
    label_replace(
        label_copy(
            #coroot:http_requests{source_namespace=~"metricsedge", source_service=~".*", target_namespace!~"(monitoring|system\\.slice)"} or on (source_pod,actual_destination) I think we do not inspect around the selected namespace
            coroot:http_requests{target_namespace=~"$namespace", target_service=~"$service"},
            "source_pod","source"
        ),
        "actual_destination","$1","actual_destination","(.+?)"
    )
  )
)

max by (container_id) ((metric * on(source,actual_destination) group_left(target_namespace,target_pod,target_service) edges_query) * on(target_pod) group_left(container_id)
group by (target_pod,container_id) (
    label_replace(
        container_restarts_total{cluster_name=~"$cluster",container_id=~"\\/k.*"},
        "target_pod", "$1", "container_id", "\\/k.+?\\/.*\\/(.+?)\\/.*"
    ) or on (target_pod,container_id) label_replace(
        container_restarts_total{cluster_name=~"$cluster",container_id=~"\\/system.*"},
        "target_pod", "$1", "container_id", "\\/sys.+?\\/(.+?)$"
    )
    ))

max_over_time(
  histogram_quantile(0.95,
    sum by (le, container_id) (
      rate(container_http_requests_duration_seconds_total_bucket{
        cluster_name=~"$cluster",
        container_id=~".*?$namespace\\/.+?$",
        container_id=~".*?\\/$service$"
      }[$__interval])
    )
  )[$__range]
)

WITH( 
    base_query = alias(
    union(
        group by (id,title,subtitle) (
            label_join(
                label_copy(
                    coroot:success_connections{source_cluster_name=~"$cluster", source_namespace=~"$namespace", source_service=~"$service", target_namespace!~"$excl_namespace"}
                    , "source_pod","id", "source_pod","title"
                ),
            "subtitle"," ", "source_namespace","source_service")
                or 
            label_join(
                label_copy(
                    coroot:success_connections{target_namespace=~"$namespace", target_service=~"$service", source_namespace!~"$excl_namespace"}
                    , "target_pod","id", "target_pod","title"
                ),
            "subtitle"," ", "target_namespace","target_service")
        ),
        (
        group by (id,title,subtitle) (
            label_join(
                label_copy(
                    coroot:success_connections{source_cluster_name=~"$cluster", source_namespace=~"$namespace", source_service=~"$service", target_namespace!~"$excl_namespace"}
                    , "target_pod","id", "target_pod","title"
                ),
            "subtitle"," ", "target_namespace","target_service")
                or 
            label_join(
                label_copy(
                    coroot:success_connections{target_namespace=~"$namespace", target_service=~"$service", source_namespace!~"$excl_namespace"}
                    , "source_pod","id", "source_pod","title"
                ),
            "subtitle"," ", "source_namespace","source_service")
            )
        )
    )
,"nodes") )

alias(base_query * on(id) group_right(title,subtitle,__name__) range_sum(
    sum by (id) (
      label_copy(
        increase(
          container_network_advance_tcp_stats_total{tcp_state=~".*drop"}[1m]
        )
        ,"pod","id"
      )
    ) 
  [$__range]
) or (base_query - 1),"nodes")

alias(
    group by(source,target,id)(
        label_join(
            label_copy(
                coroot:success_connections{source_namespace=~"$namespace", source_service=~"$service", target_namespace!~"$excl_namespace"} 
                or
                coroot:success_connections{target_namespace=~"$namespace", target_service=~"$service", source_namespace!~"$excl_namespace"},
                "source_pod","source","target_pod","target"
            ),
            "id","+","source","target"
        )
    )
,"edges")

