WITH (
  metric = max by (source, destination_ip) (
    label_replace(
      increase(container_net_latency_seconds{cluster_name=~"$cluster", container_id=~"\\/k.*"}[$__rate_interval]),
      "source", "$1", "container_id", "\\/k.+?\\/.*\\/(.+?)\\/.*"
    ) or label_replace(
      increase(container_net_latency_seconds{cluster_name=~"$cluster", container_id=~"\\/system.slice.*"}[$__rate_interval]),
      "source", "$1", "container_id", "\\/system.slice\\/(.+?)$"
    )
  ), 
  edges_query =  group by(source,destination_ip,target_namespace,target_pod,target_service)(
    label_replace(
        label_copy(
            coroot:success_connections{source_cluster_name=~"$cluster", source_namespace=~"$namespace", source_service=~"$service", target_namespace!~"$excl_namespace"}
            or on (source_pod,actual_destination)
            coroot:success_connections{target_namespace=~"$namespace", target_service=~"$service", source_namespace!~"$excl_namespace"},
            "source_pod","source"
        ),
        "destination_ip","$1","actual_destination","(.+?):.*"
    )
  )
)

topk_max($top,(metric * on(source,destination_ip) group_left(target_namespace,target_pod,target_service) edges_query))

