WITH (
  combined_metric = metric
  * on(destination_ip) group_left(namespace, service) (
    group by (destination_ip, namespace, service) (
      label_copy(
        label_join(
          label_replace(
            label_replace(
              label_replace(
                container_net_tcp_listen_info,
                "namespace", "[$1]", "container_id", "\\/.+?\\/(.+?)\\/.*"
              ),
              "service", "$1", "container_id", ".*\\/(.+?)$"
            ),
            "destination_ip", "$1", "listen_addr", "(.+?):.*"
          ),
          "namespace", " ", "namespace", "service"
        ),
        "listen_addr", "service"
      )
    )
    or on(destination_ip)
    group by (destination_ip, namespace, service) (
      label_copy(
        group by (domain) (
          container_dns_requests_total{container_id=~".+?$namespace\\/.+?$", container_id=~".+?\\/$service$"}
        ) * on(domain) group_left(ip)
        group by (domain, ip) (
          label_copy(ip_to_fqdn, "fqdn", "domain")
        ),
        "domain", "service", "ip", "destination_ip", "ip", "namespace"
      )
    )
  )
)

combined_metric

