union(
  max by (id, title, subtitle) (
  label_copy(
  # Enriched group
  (
    group by (actual_destination, instance, container_id, target, source, namespace, service) (
      label_replace(
        label_replace(
          max_over_time(container_net_tcp_active_connections{cluster_name=~"$cluster",container_id=~".+?$namespace\\/.+?$", container_id=~".+?\\/$service$"}[$__range]),
          "target", "$1", "actual_destination", "(.+?):.*"
        ),
        "source", "$1", "instance", "(.+?):.*"
      )
    )
    * on(target) group_left(namespace, service) (
      group by (target, namespace, service) (
        label_copy(
        label_join(
        label_replace(
          label_replace(
            label_replace(
              max_over_time(container_net_tcp_listen_info[$__range]),
              "namespace", "[$1]", "container_id", "\\/.+?\\/(.+?)\\/.*"
            ),
            "service", "$1", "container_id", ".*\\/(.+?)$"
          ),
          "target", "$1", "listen_addr", "(.+?):.*"
        ), "namespace", " ", "namespace","service"), "listen_addr", "service")
      )
    )
  ) 
  # Base group for missing namespace and service
  or on(target)
  (label_copy(
    group by (actual_destination, instance, container_id, target, source) (
      label_replace(
        label_replace(
          max_over_time(container_net_tcp_active_connections{cluster_name=~"$cluster",container_id=~".+?$namespace\\/.+?$", container_id=~".+?\\/$service$"}[$__range]),
          "target", "$1", "actual_destination", "(.+?):.*"
        ),
        "source", "$1", "instance", "(.+?):.*"
      )
    ), "target","namespace","actual_destination","service")
  ),  "target", "id", "namespace", "subtitle", "service", "title"
  )
),
    # The selected service itself
    group by (title, id, subtitle) (
      label_replace(
      label_replace(
        label_replace(
          max_over_time(container_net_tcp_listen_info{cluster_name=~"$cluster",container_id=~".+?$namespace\\/.+?$", container_id=~".+?\\/$service$"}[$__range])
          ,"subtitle", "$1", "container_id", "\\/.+?\\/(.+?)\\/.*"
        ),
        "id", "$1", "listen_addr", "(.+?):.*"
      ),
      "title", "$1", "container_id", ".*\\/(.+?)$")
    ),
    # clients on the left
    group by (id,title,subtitle) (
    group by (target) (
      label_replace(
        label_replace(
          label_replace(
            max_over_time(container_net_tcp_listen_info{cluster_name=~"$cluster",container_id=~".+?$namespace\\/.+?$", container_id=~".+?\\/$service$"}[$__range])
            ,"subtitle", "$1", "container_id", "\\/.+?\\/(.+?)\\/.*"
          ),
          "target", "$1", "listen_addr", "(.+?):.*"
        ),
        "title", "$1", "container_id", ".*\\/(.+?)$"
      )
    ) * on (target) group_left(id,title,subtitle)
    group by (subtitle, title, target, id) (
    label_copy(
      label_join(
        label_replace(
          label_replace(
            label_replace(
              label_replace(
                max_over_time(container_net_tcp_active_connections[$__range]),
                "target", "$1", "actual_destination", "(.+?):.*"
              ),
              "namespace", "[$1]", "container_id", "\\/.+?\\/(.+?)\\/.*"
            ),     
            "service", "$1", "container_id", ".*\\/(.+?)$"
          ),
          "id", "$1", "instance", "(.+?):.*"
        ), # not correct yet, need listen_addr here
        "subtitle", " ", "namespace", "service"
      ),
      "instance","title"
    ) # not correct yet, need listen_addr here
  )
)
)

